#version 300 es
#define PI 3.14159265359
precision highp float;
uniform float uTime;
in  vec3 vPos;
out vec4 fragColor;

// Ref. https://observablehq.com/d/94ac6e8018b5b3a1
// Ref. https://observablehq.com/d/a741f9a27e8c0e73

void main() {
   float d = dot(vPos, vPos);
   fragColor = vec4(0.,0.,0.,1.);
   float row = 20.;
   float i = 4.44;
   float j = 4.44;
   float step = 2./row;
   float cx = -1. + i * step + step/2.;
   float cy = -1. + j * step + step/2.;
   float t0 = uTime / 2.;
   if (d < .8) {
      float l = length(vec2(cx, cy));
      float theta = atan(cy, cx) / (PI * 2.);
      float rotation = mod(l + theta - t0, 1.) * PI * -2.;
      float cosa = cos(rotation);
      float sina = sin(rotation);
      vec3 dir = vec3(sina, cosa, sina + cosa);
      vec3 p = vec3(vPos.xy, sqrt(1. - d));
      float diffuse = .5 * max(0.,dot(dir, p));
      float r = .5 + .5 * sin(i / row * PI * 2.);
      float g = .5 + .5 * cos(j / row * PI * 2.);
      float b = (r + g) / 2.;
      float t = cos(rotation) * .5 + .5;
      r = r * t + (1. - t) * 1.;
      g = g * t + (1. - t) * 1.;
      b = b * t + (1. - t) * 1.;
      vec3 color = vec3(r, g, b);
      fragColor = vec4(color * vec3(diffuse), 1.);
   }
}

#version 300 es
precision highp float;
uniform float uTime;
in  vec3 vPos;
out vec4 fragColor;

void main() {
   float t = uTime / 2.;
   fragColor = vec4(0.,0.,0.,1.);
   float r = .3 + .2 * sin(4. * t);
   float x = vPos.x + .5 * sin(2.50 * t);
   float y = vPos.y + .5 * cos(3.50 * t);
   float z = sqrt(r * r - x * x - y* y);
   vec3 p = vec3(x, y, z);
   if (p.z > 0.) {
     vec3 direction = vec3(1., 1., 1.);
     float diffuse = max(0.,dot(direction, p));
     vec3 color = vec3(1., 0.25, .5);
     fragColor = vec4(vec3(diffuse) * color, 1.);
   }
}

