const n = 100;
const m = 2;

p.setup = () => {
  const size = 600;
  const step = p.TWO_PI / n;
  const d = size - 10;
  const r = d / 2;
  p.createCanvas(size, size);
  p.background(0);
  p.noFill();
  p.stroke(255);
  p.circle(p.width / 2, p.height / 2, d);
  p.translate(p.width / 2, p.height / 2);
  for(let i = 0; i < n; i++) {
    const a = i * step;
    const b = (i * m) % n * step;
    const x = r * p.cos(a);
    const y = r * p.sin(a);
    const x1 = r * p.cos(b);
    const y1 = r * p.sin(b);
    p.line(x, y, x1, y1);
  }
};

