#version 300 es
precision highp float;
uniform float uTime;
in  vec3 vPos;
out vec4 fragColor;

float turbulence(vec3 P) {
   float f = 0., s = 1.;
   for (int i = 0 ; i < 8 ; i++) {
      f += abs(noise(s * P)) / s;
      s *= 2.;
      P = vec3(.866*P.x + .5*P.z, P.y + 100., -.5*P.x + .866*P.z);
   }
   return f;
}

void main() {
   fragColor = vec4(0., 0., 0., 1.0);
   float x = vPos.x * 2.;
   float y = vPos.y * 2.;
   vec3 loop = vec3(sin(uTime), cos(uTime), 0.);
   float r = 1. + .5 * noise(vec3(x,y,0.)+loop);
   float z = sqrt(r * r - x*x - y*y);
   float cR = cos(.2*uTime), sR = sin(.2*uTime);    
   float cV = cos(.1*uTime), sV = sin(.1*uTime);
   vec3 P = vec3(cR*x+sR*z+cV,y,-sR*x+cR*z+sV);
   if (z > .0) {
     float d = .1 + .5 * max(0., x + y + z);
     float f = (.5 +.5 * sin((x*x + y*y) * 5. + turbulence(P) * 12.));
     vec3 c = vec3(d) * vec3(f*f, f, 1.);
     fragColor = vec4(sqrt(c), 1.);
   }
}
